package testing;

public class evennum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 34;
		
		//checking whether the number is even or odd
		
		if(number % 2 == 0)
			System.out.println(number + " is even");
		
		else
			
			System.out.println(number + " is odd");
		
	}

}
